import React from "react";
import { ChevronRight } from "lucide-react";
import heroVideo from "../../assets/hero-video.mp4";

const HomePage = ({ onStart }) => {
  return (
    <div className="flex flex-col min-h-screen page-transition">

      {/* HERO SECTION */}
      <div
        className="relative bg-black overflow-hidden"
        style={{ height: "calc(100vh - 64px)" }}
      >
        {/* VIDEO BACKGROUND */}
        <video
          className="absolute inset-0 w-full h-full object-cover"
          src={heroVideo}
          autoPlay
          muted
          loop
          playsInline
        />

        {/* DARK OVERLAY */}
        <div className="absolute inset-0 bg-black/60" />

        {/* CENTERED CONTENT */}
        <div className="absolute inset-0 flex items-center justify-center text-white px-6">
          <div className="max-w-3xl text-center animate-slide-up">
            <h1 className="text-5xl font-bold mb-4">
              Welcome to <span className="text-[#FF0000]">ABB LV Drives Pro</span>
            </h1>

            <h2 className="text-xl text-gray-300 mb-8">
              Higher efficiency. Lower consumption. Simplify finding the right drive for your applications.
            </h2>

            <button
              onClick={onStart}
              className="bg-[#FF0000] hover:bg-red-700 text-white px-8 py-4 rounded font-bold text-lg inline-flex items-center gap-2 transition-all shadow-lg transform hover:scale-105"
            >
              Start Your Selection <ChevronRight />
            </button>
          </div>
        </div>
      </div>

      {/* FOOTER */}
      <footer className="bg-gray-950 text-gray-500 py-6 text-xs border-t border-gray-800">
        <div className="max-w-6xl mx-auto px-6 flex justify-between">
          <div>Â© 2026 ABB, All Rights Reserved</div>
        </div>
      </footer>
    </div>
  );
};

export default HomePage;
